# Definir las dimensiones de la imagen
width = 32
height = 32

# Asegurarse de que la cantidad de datos coincida con las dimensiones
if len(data) != width * height:
    raise ValueError(f"La cantidad de datos ({len(data)}) no coincide con las dimensiones {width}x{height} ({width * height}).")

# Convertir la lista en un array de NumPy y darle la forma de la imagen
