# Crear una imagen en escala de grises a partir del array
# 'L' indica modo de escala de grises (8 bits por píxel)
image = Image.fromarray(image_array, mode='L')

# Guardar la imagen
output_filename = "imagen_gris1_32x32.png"
image.save(output_filename)

print(f"La imagen en escala de grises de 32x32 se ha guardado como '{output_filename}'")

